mapper.select:
select zwmc_bm as jcxmc, ywmc,id, pdnh,if_pd,yyckjz,bl,jcff,jcyjmc,jcyj,pdyj, pdyjmc,wswnz,wswcz,wswmz,xlz,xlzmrz, bzffjcxdw as jcxdw,bzzxyxx as zxyxx,bzzxyxxdw as zxyxxdw,bzzdyxx as zdyxx,bzzdyxxdw as zdyxxdw, if_xtpd,jg,if_bzff,if_cma,if_cmaf,if_cnas,if_catl, zbzl,zbzldw,bz from t_jcxm_jbxx
zwmc_bm like concat('%',#{map.jcxmc},'%')
order by id desc limit #{map.start},#{map.length};

select count(*)  from
t_jcxm_jbxx


        zwmc_bm like concat('%',#{map.jcxmc},'%');
